mapper.select:
SELECT cur_node_id AS curCheckId, per_node_id AS preCheckId, node_description AS description
FROM check_system
WHERE per_node_id = #{curNodeId}

SELECT cur_node_id AS curCheckId, per_node_id AS preCheckId, node_description AS description
FROM check_system
WHERE cur_node_id = #{nodeId}

mapper.update:
UPDATE check_system
SET node_description = #{description}
WHERE cur_node_id = #{nodeId}
