mapper.select:
SELECT user_id AS id
FROM login_info
WHERE user_id = #{id} AND user_password = #{password} AND user_type = #{type}

mapper.update:
UPDATE login_info
SET user_password = #{password}
WHERE user_id = #{id} AND user_type = #{type}

mapper.select:
SELECT staff_id AS id, staff_name AS name, staff_gender AS gender, staff_phone AS phone, staff_group_id AS groupId, staff_leader_flag AS leaderFlag
FROM staff_info
WHERE staff_id = #{id}

mapper.update:
UPDATE staff_info
SET staff_name = #{name}, staff_gender = #{gender}, staff_phone = #{phone}
WHERE staff_id = #{id}
