mapper.select:
SELECT user_id AS id
FROM login_info
WHERE user_id = #{id} AND user_password = #{password} AND user_type = #{type}

SELECT tenant_id AS id, tenant_name AS name
FROM tenant_info
WHERE tenant_id = #{id}

SELECT user_password AS password
FROM login_info
WHERE user_id = #{id} AND user_type = #{type}

mapper.update:
UPDATE login_info
SET user_password = #{password}
WHERE user_id = #{id} AND user_type = #{type}
